import { useState, useEffect } from 'react';

let users = [];

export const addUser = (email) => {
  const user = { email, invited_at: new Date(), provisioned: false };
  users.push(user);
};

export const requestProvisioning = (email) => {
  const user = users.find(u => u.email === email);
  if (user) {
    user.requested = true;
  }
};

export const getUnprovisionedUsers = () => {
  const threshold = new Date(Date.now() - 24 * 60 * 60 * 1000);
  return users.filter(user => !user.provisioned && user.invited_at < threshold);
};

export const useUserData = () => {
  const [userData, setUserData] = useState({ users: [], unprovisionedUsers: [] });

  useEffect(() => {
    const interval = setInterval(() => {
      setUserData({
        users: users,
        unprovisionedUsers: getUnprovisionedUsers()
      });
    }, 1000); // Check every second for demo purposes

    return () => clearInterval(interval);
  }, []);

  return userData;
};
