import React, { useState, useEffect } from 'react';
import SSOProvisioningNotification from './components/SSOProvisioningNotification';
import SelfServicePortal from './components/SelfServicePortal';
import simulateBackend from './utils/ssoChecker';
import './styles/App.css';

const App = () => {
  const [pendingUsers, setPendingUsers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    try {
      const unprovisionedUsers = simulateBackend();
      setPendingUsers(unprovisionedUsers);
    } catch (err) {
      setError('Failed to fetch unprovisioned users. Please try again later.');
      console.error('Error fetching unprovisioned users:', err);
    }
  }, []);

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="container">
      <h1>Asana SSO Provisioning Dashboard</h1>
      <SSOProvisioningNotification users={pendingUsers} />
      <SelfServicePortal />
    </div>
  );
};

export default App;
